INSERT INTO `sn_task_detail` VALUES ('203', '2', 'DEL_AB_BAK', 'DELETE ZKBAK ;', '0', '1', '2016-11-12 16:29:40');
INSERT INTO `sn_task_detail` VALUES ('204', '2', 'AB_BAK', 'INSERT INTO ZKBAK(BBH,HYNO,TEL,CCBH)\r\nSELECT    distinct \'zk\',a.HYNO AS HYNO,A.TEL AS TEL,a.blockno\r\nFROM CALL010 a \r\nLEFT JOIN FMD060 D ON A.HYNO=D.HYNO \r\nLEFT JOIN FMD070 c ON A.SALONNO =c.SLNCD \r\nleft join FHD030 E on a.HYNO=E.HYNO \r\nLEFT JOIN FMD010 A0 ON A.HYNO =A0.HYNO \r\nWHERE \r\n--查询范围\r\n--除外条件--抽出コード4639～4647の最新コール結果が「OK」と「不在」の方を抽出してしてください\r\na.SALONNO in (\'102010\',\'102011\')\r\n--②「除外」を除く \r\nAND ISNULL(A.HYNO,\'\') not in (select distinct HYNO from FMD200 )  \r\n--④元支店長、元サロン長、Ｂ会員、友の会会員(会員№4000000からの方）\r\nAND A.HYNO NOT IN ( SELECT DISTINCT SLNKAINO  FROM FMD070 WHERE  ISNULL(SLNDELDT,\'\')<>\'00000000\') \r\nAND A.HYZB<>\'B\' AND SUBSTRING (A.HYNO,1,1) <>\'4\'    \r\n--⑤2016年4月～現在まで購入のある方（直近購入） \r\nAND isnull(D.zjgrr,\'\') < \'20160401\'  \r\n--⑥80歳以上の方（1936年12月31日までに生まれた人） \r\nAND ISNULL(a.SNYR,\'\')>\'19361231\'  \r\n--⑦【岩手県青森県コール保留地域】\r\nAND ISNULL(A.HYNO,\'\') NOT IN (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'H49H\') ', '0', '1', '2016-11-12 16:29:41');
INSERT INTO `sn_task_detail` VALUES ('205', '2', 'InsertD', 'insert into FMD180EXCEL1(ccbh,HYNO)\r\nSELECT \'v92D\',A.HYNO\r\nfrom zkbak A \r\nleft join FHD030 B on A.HYNO =B.HYNO \r\nwhere ISNULL(b.ztqf,\'\') not in (\'\',\'1\',\'2\') ;\r\n', '0', '1', '2016-11-12 16:29:47');
INSERT INTO `sn_task_detail` VALUES ('206', '2', 'D', 'select B.SALONNO,\r\n    CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY B.SALONNO ORDER BY B.SALONNO, A.HYNO))) AS SEQ, A.HYNO,A.ztqf,A.phase,A.ccbh\r\nfrom FHD030 A\r\nLEFT JOIN CALL010 B ON A.HYNO=B.HYNO\r\nwhere A.HYNO in (SELECT DISTINCT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'v92D\')', '80', '1', '2016-11-12 16:29:47');
INSERT INTO `sn_task_detail` VALUES ('207', '2', 'InsertE', 'insert into FMD180EXCEL1(ccbh,HYNO)\r\nSELECT \'v92E\',A.HYNO\r\nfrom zkbak A \r\nleft join FHD030 B on A.HYNO =B.HYNO \r\nwhere isnull(b.ztqf,\'\')+ ISNULL (b.phase,\'\') in (\'112\',\'212\')', '0', '1', '2016-11-12 16:29:53');
INSERT INTO `sn_task_detail` VALUES ('208', '2', 'E', 'select B.SALONNO,\r\n    CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY B.SALONNO ORDER BY B.SALONNO, A.HYNO))) AS SEQ, A.HYNO,A.ztqf,A.phase,A.ccbh\r\nfrom FHD030 A\r\nLEFT JOIN CALL010 B ON A.HYNO=B.HYNO\r\nwhere A.HYNO in (SELECT DISTINCT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'v92E\')', '0', '1', '2016-11-12 16:29:53');
INSERT INTO `sn_task_detail` VALUES ('209', '2', 'InsertF', 'insert into FMD180EXCEL1(ccbh,HYNO)\r\nselect \'v92F\' ,hyno \r\nfrom zkbak  where HYNO+REPLACE(rtrim(ltrim(REPLACE(tel,\'-\',\'\'))),\' \',\'\') \r\nin ((select [会員№]+REPLACE(rtrim(ltrim([電話番号（ハイフンなし）])),\' \',\'\')    \r\nfrom [不備電話$]));', '0', '1', '2016-11-12 16:29:54');
INSERT INTO `sn_task_detail` VALUES ('210', '2', 'F', 'select SALONNO,\r\n    CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY A.SALONNO ORDER BY A.SALONNO, A.HYNO))) AS SEQ,hyno ,TEL\r\nfrom call010 A\r\nwhere HYNO in (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'v92F\')\r\nORDER BY SALONNO,HYNO ', '24', '1', '2016-11-12 16:29:54');
INSERT INTO `sn_task_detail` VALUES ('211', '2', 'InsertG', 'insert into FMD180EXCEL1(hyno,ccbh)\r\nSELECT HYNO,\'v92G\' FROM(\r\nSELECT (SELECT COUNT(1) FROM [dbo].[f_split](TEL,\'-\')) AS ID,* \r\nFROM zkbak ) TT WHERE TT.ID<>3 ;', '0', '1', '2016-11-12 16:29:54');
INSERT INTO `sn_task_detail` VALUES ('212', '2', 'G', 'select A.SALONNO,\r\n    CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY A.SALONNO ORDER BY A.SALONNO, A.HYNO))) AS SEQ, A.hyno ,A.TEL\r\nfrom call010 A\r\nwhere A.HYNO in (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'v92G\')\r\nORDER BY A.SALONNO,A.HYNO', '0', '1', '2016-11-12 16:29:54');
INSERT INTO `sn_task_detail` VALUES ('213', '2', 'InsertH', 'insert into FMD180EXCEL1(hyno,ccbh)\r\nSELECT  TT.HYNO,\'v92H\' FROM \r\n(SELECT C.HYNO,CASE WHEN ISNULL(D.TEL,\'\') = \'\' THEN C.TEL ELSE D.TEL END AS TEL FROM \r\n(SELECT DIS.* FROM \r\n(SELECT  V1.HYNO AS HYNO,V1.TEL AS TEL FROM \r\n(select HYNO,TEL from zkbak) V1 ) DIS ) C\r\nLEFT JOIN(SELECT HYNO,TEL,HYM FROM FHD030) D ON C.HYNO = D.HYNO ) TT\r\nWHERE REPLACE(TT.TEL,\'-\',\'\') IN (SELECT REPLACE(AA.TEL, \'-\', \'\')\r\nFROM (SELECT CASE WHEN ISNULL(B.TEL, \'\') = \'\' THEN A.TEL ELSE B.TEL END AS TEL\r\nFROM (SELECT DISTINCT  HYNO, TEL FROM \r\n(select HYNO,TEL from zkbak) TM ) A\r\nLEFT JOIN (SELECT HYNO,TEL FROM FHD030) B ON A.HYNO = B.HYNO ) AA\r\nWHERE ISNULL(AA.TEL, \'\') <> \'\'\r\nGROUP BY REPLACE(AA.TEL, \'-\', \'\')\r\nHAVING COUNT(AA.TEL) > 1)  ', '0', '1', '2016-11-12 16:30:05');
INSERT INTO `sn_task_detail` VALUES ('214', '2', 'H', 'select A.SALONNO,\r\n    CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY A.SALONNO ORDER BY A.SALONNO, A.HYNO))) AS SEQ, A.hyno ,A.TEL\r\nfrom call010 A\r\nwhere A.HYNO in (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'v92H\')\r\nORDER BY A.SALONNO,A.HYNO', '13', '1', '2016-11-12 16:30:05');
INSERT INTO `sn_task_detail` VALUES ('215', '2', 'AH', 'SELECT distinct \r\n   CONVERT(INT,CONVERT(NVARCHAR,ROW_NUMBER()  OVER(PARTITION BY A.SALONNO ORDER BY A.SALONNO))) AS SEQ, \r\n     A.SALONNO,A.HYNO,A.HYZB,A.HYM,A.HYMKN ,A.CHGRSLNO,ISNULL(C2.SLNMC,\'\')CHSLNMC,ISNULL(B.MC,\'\') ZS,\r\n    A.SNYR,ISNULL(D.ZJGRJE,\'\') ZJGRJE,ISNULL(D.ZJGRR,\'\') ZJGRR,isnull(c.SLNDELDT,\'\'),A.TEL,\r\n    a.QHYNO,A.QHYM,ISNULL(E.DLDLLQF,\'\') DLDLLQF ,\r\n    ISNULL(E.ZTQF,\'\') ZTQF ,ISNULL(E.PHASE,\'\') PHASE,E.CCBH,E.QHCALLRS,\r\n A.BLOCKNO,F.MC\r\nFROM  CALL010 A \r\nLEFT JOIN FMD010 A0 ON A.HYNO =A0.HYNO \r\nLEFT JOIN FMD080 B ON  A.DDFXBH=B.ID\r\nLEFT JOIN FMD070 C ON A.SALONNO=C.SLNCD\r\nLEFT JOIN FMD070 C2 ON A.CHGRSLNO =C2.SLNCD\r\nLEFT JOIN FMD060 D ON A.HYNO=D.HYNO \r\nLEFT JOIN FHD030 E ON A.HYNO=E.HYNO \r\nLEFT JOIN FMD110 F ON A.BLOCKNO = F.MCKEY AND F.GLBH = \'004\' \r\nWHERE\r\n--抽出コード4639～4647の最新コール結果が「OK」と「不在」の方を抽出してしてください\r\na.SALONNO in (\'102010\',\'102011\')\r\n\r\n--②「除外」を除く \r\nAND ISNULL(A.HYNO,\'\') not in (select distinct HYNO from FMD200 )  \r\n--④元支店長、元サロン長、Ｂ会員、友の会会員(会員№4000000からの方）\r\nAND A.HYNO NOT IN ( SELECT DISTINCT SLNKAINO  FROM FMD070 WHERE  ISNULL(SLNDELDT,\'\')<>\'00000000\') \r\nAND A.HYZB<>\'B\' AND SUBSTRING (A.HYNO,1,1) <>\'4\'    \r\n--⑤2016年4月～現在まで購入のある方（直近購入） \r\nAND isnull(D.zjgrr,\'\') < \'20160401\'  \r\n--⑥80歳以上の方（1936年12月31日までに生まれた人） \r\nAND ISNULL(a.SNYR,\'\')>\'19361231\'  \r\n--⑦【岩手県青森県コール保留地域】\r\nAND ISNULL(A.HYNO,\'\') NOT IN (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'H49H\') AND A.HYNO NOT IN (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH in (\'v92D\',\'v92E\',\'v92F\',\'v92G\',\'v92H\'))\r\n', '131', '1', '2016-11-12 16:30:07');
INSERT INTO `sn_task_detail` VALUES ('216', '2', 'DL', 'SELECT \r\ndistinct A.hyno \r\nFROM  CALL010 A    \r\nLEFT JOIN FMD060 D ON A.HYNO=D.HYNO    \r\nLEFT JOIN FHD030 E ON A.HYNO=E.HYNO  \r\nWHERE\r\n\r\na.SALONNO in (\'102010\',\'102011\')\r\n\r\nAND ISNULL(A.HYNO,\'\') not in (select distinct HYNO from FMD200 )  \r\nAND A.HYNO NOT IN ( SELECT DISTINCT SLNKAINO  FROM FMD070 WHERE  ISNULL(SLNDELDT,\'\')<>\'00000000\') \r\nAND A.HYZB<>\'B\' AND SUBSTRING (A.HYNO,1,1) <>\'4\'    \r\nAND isnull(D.zjgrr,\'\') < \'20160401\'  \r\nAND ISNULL(a.SNYR,\'\')>\'19361231\'  \r\nAND ISNULL(A.HYNO,\'\') NOT IN (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH=\'H49H\') AND A.HYNO NOT IN (SELECT HYNO FROM FMD180EXCEL1 WHERE CCBH in (\'v92D\',\'v92E\',\'v92F\',\'v92G\',\'v92H\'))\r\n', '0', '1', '2016-11-12 16:30:08');
